<template>    GmapMap(        :center="center"        :zoom="zoom"        class="google_maps_container"        id="eventsFeedMap")      div  (v-for='(item, index) in events')      div (v-for='(item, index) in item.events'           :isLike='item.like'           :key='index')        gmap-info-window (:options="infoOptions"                           :position="infoWindowPos"                           :opened="infoWinOpen"                           @closeclick="infoWinOpen=false")           .window              .image                .img (@click="openFullEvent($event, item)"                      :style='{ background: `url(${item.photos[0]}) no-repeat center` }')                  .like (@click='likeEvent(item)')               .info (style="width: 250px;")                  .main_info                    .mode {{ item.sport }}                      res {{ item.title }}                      .date {{ item.date | moment('DD/MM/YYYY') }}                   .price (v-if='item.price') {{ item.price }}                   .price (v-else) Бесплатно                    <!-- //- div (v-for="(m,i) in item.item.events") -->                     span {{item.city.coordinates[0]}}                     gmap-marker (:key="index"                        :position="{lat:item.city.coordinates[0], lng:item.city.coordinates[1]}"                        :clickable="true" )</template><script>    import { mapGetters, mapActions } from 'vuex';    import { loaded } from 'vue2-google-maps';    export default {        props: ['events'],        data() {          // const lat =  10.11;          // const lng = 12.11;            return {                              center: {                    lat: 10.11,                    lng: 12.11,                },                zoom: 16,                infoContent: '',                infoWindowPos: null,                infoWinOpen: false,                currentMidx: null,                infoOptions: {                    pixelOffset: {                        width: 0,                        height: -35                    }                },                markers: [{                    position: {                        lat: 10.11,                        lng: 12.11,                    }                }]            }        },        methods: {          ...mapActions([            'likeEvent',          ]),          likeEvent() {            console.log('like')          },          openFullEvent({ target }, { event_id }) {            if (target.className !== 'likeEvents') {              this.$router.push(`/events/${event_id}`)            }          },          toggleInfoWindow: function(marker, idx) {              this.infoWindowPos = marker.position;              this.infoContent = marker.infoText;              //check if its the same marker that was selected if yes toggle              if (this.currentMidx == idx) {                this.infoWinOpen = !this.infoWinOpen;              }              //if different marker set infowindow to open and reset current marker index              else {                this.infoWinOpen = true;                this.currentMidx = idx;              }            }        },        computed: {          ...mapGetters([            'eventFeedView'          ]),        },        created() {            console.log(this.events);        }    }</script><style lang="scss">    #eventsFeedMap  {      width: 100%;      height: 500px;      & .window {          display: flex;          flex-direction: row;          width: 345px;          height: 516px;          justify-content: space-between;          background: #fff;          .image {             width: 50%;             height: 100%;             flex: 0 1 100%;             border-radius: 0;             position: relative;             cursor: pointer;             .img {              width: 100%;              height: 100%;             }             .like {                position: absolute;                top:5px;                left: 5px;                z-index: 10;                width: 20px;                height: 20px;                background: url(../../../static/images/likeEvent.svg) no-repeat center;                background-size: cover;             }          }      }      .info {        align-self: center;        margin-left: 15px;        span {          color: darken($gray, 25%);          display: block;          margin-bottom: 5px;          font-size: 15px;          &.mode{            font-family: 'SF UI Display Bold';            text-transform: uppercase;            font-size: 14px;          }          &.res {              font-family: 'GT Walsheim Pro Bold';            color: $darkBlue;          }          &.date {            font-family: 'SF UI Display Medium';          }        }        .price {          font-family: 'SF UI Display Semibold';          color: $green;        }      }      .gm-style-iw {        top: 1px !important;        left: 0 !important;        bottom: 0;        right: 0;        width: 345px;        height: 100px;        overflow: visible !important;                div {          overflow: visible !important;        }      }    }</style>